import { useState } from "react";
import { Button } from "@/components/ui/button";
import { ChevronLeft, ChevronRight } from "lucide-react";

interface QuizQuestionProps {
  question: {
    id: string;
    prompt: string;
    a: string;
    b: string;
    c: string;
    d: string;
    order: number;
  };
  currentQuestion: number;
  totalQuestions: number;
  selectedAnswer: string | null;
  onAnswerSelect: (answer: string) => void;
  onPrevious: () => void;
  onNext: () => void;
  onSubmit: () => void;
  canGoNext: boolean;
  canGoPrevious: boolean;
  isLastQuestion: boolean;
}

export default function QuizQuestion({
  question,
  currentQuestion,
  totalQuestions,
  selectedAnswer,
  onAnswerSelect,
  onPrevious,
  onNext,
  onSubmit,
  canGoNext,
  canGoPrevious,
  isLastQuestion
}: QuizQuestionProps) {
  const progressPercentage = (currentQuestion / totalQuestions) * 100;
  const options = [
    { key: 'a', text: question.a },
    { key: 'b', text: question.b },
    { key: 'c', text: question.c },
    { key: 'd', text: question.d }
  ];

  return (
    <div className="p-6">
      {/* Progress indicator */}
      <div className="mb-6">
        <div className="flex items-center justify-between mb-2">
          <span className="text-sm font-medium text-slate-700" data-testid="text-question-progress">
            Question {currentQuestion} of {totalQuestions}
          </span>
          <span className="text-sm text-slate-500" data-testid="text-completion-percentage">
            {Math.round(progressPercentage)}% Complete
          </span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div 
            className="bg-primary-500 h-2 rounded-full transition-all" 
            style={{ width: `${progressPercentage}%` }}
            data-testid="progress-bar"
          ></div>
        </div>
      </div>

      {/* Question */}
      <div className="mb-8">
        <h3 className="text-lg font-semibold text-slate-900 mb-4" data-testid="text-question-prompt">
          {question.prompt}
        </h3>

        <div className="space-y-3">
          {options.map((option) => (
            <label 
              key={option.key}
              className={`flex items-center p-4 border rounded-lg cursor-pointer transition-colors ${
                selectedAnswer === option.key
                  ? "border-primary-200 bg-primary-50"
                  : "border-gray-200 hover:bg-gray-50"
              }`}
              data-testid={`option-${option.key}`}
            >
              <input 
                type="radio" 
                name={`question-${question.id}`}
                value={option.key}
                checked={selectedAnswer === option.key}
                onChange={() => onAnswerSelect(option.key)}
                className="sr-only"
              />
              <div className={`flex items-center justify-center w-5 h-5 border-2 rounded-full mr-4 ${
                selectedAnswer === option.key
                  ? "border-primary-500"
                  : "border-gray-300"
              }`}>
                {selectedAnswer === option.key && (
                  <div className="w-2 h-2 bg-primary-500 rounded-full"></div>
                )}
              </div>
              <span className={`text-slate-700 ${selectedAnswer === option.key ? 'font-medium' : ''}`}>
                {option.text}
              </span>
            </label>
          ))}
        </div>
      </div>

      {/* Navigation */}
      <div className="flex items-center justify-between">
        <Button 
          variant="outline"
          onClick={onPrevious}
          disabled={!canGoPrevious}
          className="flex items-center"
          data-testid="button-previous"
        >
          <ChevronLeft className="w-4 h-4 mr-2" />
          Previous
        </Button>

        <div className="flex space-x-2">
          {Array.from({ length: totalQuestions }, (_, i) => (
            <button
              key={i}
              className={`w-8 h-8 rounded-full text-sm font-medium ${
                i + 1 < currentQuestion
                  ? "bg-ocean-500 text-white"
                  : i + 1 === currentQuestion
                  ? "bg-primary-500 text-white"
                  : "border border-gray-300 text-slate-600 hover:border-gray-400"
              }`}
              data-testid={`button-question-${i + 1}`}
            >
              {i + 1}
            </button>
          ))}
        </div>

        {isLastQuestion ? (
          <Button 
            onClick={onSubmit}
            disabled={!selectedAnswer}
            className="bg-ocean-500 hover:bg-ocean-600 text-white"
            data-testid="button-submit"
          >
            Submit Quiz
          </Button>
        ) : (
          <Button 
            onClick={onNext}
            disabled={!canGoNext}
            className="flex items-center bg-primary-500 hover:bg-primary-600 text-white"
            data-testid="button-next"
          >
            Next
            <ChevronRight className="w-4 h-4 ml-2" />
          </Button>
        )}
      </div>
    </div>
  );
}

client/src/components/track-card.tsx.content:
import { Button } from "@/components/ui/button";
import { BookOpen, CheckCircle } from "lucide-react";
import { Link } from "wouter";

interface TrackCardProps {
  track: {
    id: string;
    title: string;
    slug: string;
    summary: string | null;
    isPublished: boolean;
  };
  progress?: {
    completed: number;
    total: number;
  };
  isLocked?: boolean;
}

export default function TrackCard({ track, progress, isLocked }: TrackCardProps) {
  const progressPercentage = progress ? (progress.completed / progress.total) * 100 : 0;

  return (
    <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden hover:shadow-md transition-shadow" data-testid={`card-track-${track.id}`}>
      <div className="p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex-1">
            <h3 className="text-xl font-semibold text-slate-900 mb-2" data-testid={`text-track-title-${track.id}`}>
              {track.title}
            </h3>
            <p className="text-slate-600 text-sm mb-4" data-testid={`text-track-summary-${track.id}`}>
              {track.summary}
            </p>
          </div>
          <div className="flex items-center space-x-2">
            <span className={`px-2 py-1 text-xs font-medium rounded-full ${
              isLocked 
                ? "bg-gray-100 text-gray-700" 
                : "bg-ocean-100 text-ocean-700"
            }`}>
              {isLocked ? "Locked" : "Active"}
            </span>
          </div>
        </div>

        {progress && (
          <div className="mb-4">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-slate-600">Progress</span>
              <span className="text-sm font-medium text-slate-900" data-testid={`text-progress-${track.id}`}>
                {progress.completed}/{progress.total} lessons
              </span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className={`h-2 rounded-full ${isLocked ? "bg-gray-300" : "bg-primary-500"}`}
                style={{ width: `${progressPercentage}%` }}
              ></div>
            </div>
          </div>
        )}

        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4 text-sm text-slate-500">
            <span className="flex items-center">
              <BookOpen className="w-4 h-4 mr-1" />
              {progress?.total || 0} lessons
            </span>
            <span className="flex items-center">
              <CheckCircle className="w-4 h-4 mr-1" />
              3 quizzes
            </span>
          </div>
          {isLocked ? (
            <Button 
              variant="outline" 
              disabled
              className="text-slate-400 cursor-not-allowed text-sm"
              data-testid={`button-locked-${track.id}`}
            >
              Complete Prerequisites
            </Button>
          ) : (
            <Link href={`/tracks/${track.slug}`}>
              <Button 
                className="bg-primary-500 hover:bg-primary-600 text-white px-4 py-2 rounded-lg font-medium text-sm"
                data-testid={`button-continue-${track.id}`}
              >
                Continue
              </Button>
            </Link>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

