client/src/pages/quiz.tsx.content:
import { useState, useEffect, useCallback } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useRoute } from "wouter";
import Navigation from "@/components/navigation";
import QuizQuestion from "@/components/quiz-question";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { AlertCircle, Clock } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import type { Quiz, Question } from "@shared/schema";

type QuizWithQuestions = Quiz & { questions: Question[] };

export default function Quiz() {
  const [, params] = useRoute("/lessons/:id/quiz");
  const [currentQuestion, setCurrentQuestion] = useState(1);
  const [answers, setAnswers] = useState<Record<string, string>>({});
  const [timeRemaining, setTimeRemaining] = useState<number | null>(null);
  const [quizStarted, setQuizStarted] = useState(false);
  const [quizSubmitted, setQuizSubmitted] = useState(false);
  const [showTimeWarning, setShowTimeWarning] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: quiz, isLoading } = useQuery<QuizWithQuestions>({
    queryKey: ["/api/quizzes/lesson", params?.id],
    enabled: !!params?.id,
    onSuccess: (data) => {
      if (data && !quizStarted && timeRemaining === null) {
        const timeLimit = data.timeLimit || 30; // Default 30 minutes
        setTimeRemaining(timeLimit * 60); // Convert to seconds
      }
    },
  });

  const submitAttemptMutation = useMutation({
    mutationFn: async (attemptData: any) => {
      return apiRequest("POST", "/api/quiz-attempts", attemptData);
    },
    onSuccess: () => {
      toast({
        title: "Quiz Submitted!",
        description: "Your quiz has been submitted successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit quiz. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Auto-submit quiz function
  const autoSubmitQuiz = useCallback(() => {
    if (!quizSubmitted) {
      const score = Object.entries(answers).reduce((correct, [questionId, answer]) => {
        const question = quiz?.questions.find((q: any) => q.id === questionId);
        return question?.answer === answer ? correct + 1 : correct;
      }, 0);

      submitAttemptMutation.mutate({
        userId: "current-user",
        quizId: quiz?.id,
        score,
        totalQuestions: quiz?.questions.length || 0,
        answers: JSON.stringify(answers),
      });

      toast({
        title: "Time's Up!",
        description: "Quiz has been automatically submitted.",
        variant: "destructive",
      });
    }
  }, [quizSubmitted, answers, quiz, submitAttemptMutation, toast]);

  // Timer countdown effect
  useEffect(() => {
    if (!quizStarted || timeRemaining === null || timeRemaining <= 0 || quizSubmitted) {
      return;
    }

    const timer = setInterval(() => {
      setTimeRemaining((prev) => {
        if (prev === null || prev <= 1) {
          autoSubmitQuiz();
          return 0;
        }

        // Show warning at 5 minutes (300 seconds)
        if (prev === 301) {
          setShowTimeWarning(true);
          toast({
            title: "Time Warning!",
            description: "5 minutes remaining in your exam.",
            variant: "destructive",
          });
        }

        // Show final warning at 1 minute (60 seconds)
        if (prev === 61) {
          toast({
            title: "Final Warning!",
            description: "1 minute remaining in your exam.",
            variant: "destructive",
          });
        }

        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [quizStarted, timeRemaining, quizSubmitted, autoSubmitQuiz, toast]);

  // Start quiz function
  const startQuiz = () => {
    setQuizStarted(true);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 text-slate-900 font-sans">
        <Navigation />
        <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
            <div className="animate-pulse">
              <div className="h-6 bg-gray-200 rounded w-1/3 mb-4"></div>
              <div className="h-2 bg-gray-200 rounded w-full mb-6"></div>
              <div className="h-4 bg-gray-200 rounded w-3/4 mb-4"></div>
              <div className="space-y-2">
                {[1, 2, 3, 4].map((i) => (
                  <div key={i} className="h-12 bg-gray-200 rounded"></div>
                ))}
              </div>
            </div>
          </div>
        </main>
      </div>
    );
  }

  if (!quiz || !quiz.questions || quiz.questions.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 text-slate-900 font-sans">
        <Navigation />
        <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center py-12">
            <p className="text-slate-500" data-testid="text-quiz-not-found">
              Quiz not available for this lesson.
            </p>
          </div>
        </main>
      </div>
    );
  }

  const currentQuestionData = quiz.questions[currentQuestion - 1];
  const selectedAnswer = answers[currentQuestionData.id];
  const totalQuestions = quiz.questions.length;

  const formatTime = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const handleAnswerSelect = (answer: string) => {
    setAnswers(prev => ({
      ...prev,
      [currentQuestionData.id]: answer
    }));
  };

  const handleNext = () => {
    if (currentQuestion < totalQuestions) {
      setCurrentQuestion(prev => prev + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 1) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const handleSubmit = () => {
    const score = Object.entries(answers).reduce((correct, [questionId, answer]) => {
      const question = quiz.questions.find((q: any) => q.id === questionId);
      return question?.answer === answer ? correct + 1 : correct;
    }, 0);

    submitAttemptMutation.mutate({
      userId: "current-user", // This would come from auth context
      quizId: quiz.id,
      score,
      totalQuestions: quiz.questions.length,
      answers: JSON.stringify(answers),
    });
  };

  return (
    <div className="min-h-screen bg-gray-50 text-slate-900 font-sans">
      <Navigation />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <section className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
          <div className="border-b border-gray-200 px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-xl font-semibold text-slate-900" data-testid="text-quiz-title">
                  {quiz.title}
                </h2>
                <p className="text-sm text-slate-500" data-testid="text-quiz-meta">
                  {totalQuestions} questions â€¢ {quiz.timeLimit || 10} minutes
                </p>
              </div>
              <div className="text-right">
                <div className="text-sm text-slate-500">Time Remaining</div>
                <div className="text-lg font-mono font-semibold text-slate-900" data-testid="text-time-remaining">
                  {formatTime(timeRemaining)}
                </div>
              </div>
            </div>
          </div>

          <QuizQuestion
            question={currentQuestionData}
            currentQuestion={currentQuestion}
            totalQuestions={totalQuestions}
            selectedAnswer={selectedAnswer}
            onAnswerSelect={handleAnswerSelect}
            onPrevious={handlePrevious}
            onNext={handleNext}
            onSubmit={handleSubmit}
            canGoNext={!!selectedAnswer}
            canGoPrevious={currentQuestion > 1}
            isLastQuestion={currentQuestion === totalQuestions}
          />
        </section>
      </main>
    </div>
  );
}

client/src/pages/signin.tsx.content:
import { useState, useEffect } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Checkbox } from "@/components/ui/checkbox";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { LayoutDashboard, Mail, Eye, EyeOff, AlertCircle } from "lucide-react";
import { Link } from "wouter";
import diverWellLogo from "@assets/DIVER_WELL_TRAINING-500x500-rbg-preview_1756088331820.png";

export default function SignIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [isCredentials, setIsCredentials] = useState(true);
  const [magicLinkSent, setMagicLinkSent] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const { toast } = useToast();

  // Load remembered credentials on component mount
  useEffect(() => {
    const rememberedEmail = localStorage.getItem('rememberedEmail');
    const rememberedPassword = localStorage.getItem('rememberedPassword');
    if (rememberedEmail) {
      setEmail(rememberedEmail);
      setRememberMe(true);
    }
    if (rememberedPassword) {
      setPassword(rememberedPassword);
    }
  }, []);

  const credentialsSignInMutation = useMutation({
    mutationFn: async (credentials: { email: string; password: string; rememberMe: boolean }) => {
      return apiRequest("POST", "/api/auth/credentials", credentials);
    },
    onSuccess: (data) => {
      // Handle remember me functionality
      if (rememberMe) {
        localStorage.setItem('rememberedEmail', email);
        localStorage.setItem('rememberedPassword', password);
        localStorage.setItem('userEmail', email); // For current session
      } else {
        localStorage.removeItem('rememberedEmail');
        localStorage.removeItem('rememberedPassword');
        localStorage.setItem('userEmail', email); // Still set for current session
      }

      toast({
        title: "Welcome back!",
        description: "You have been signed in successfully.",
      });
      // Redirect to dashboard
      window.location.href = '/dashboard';
    },
    onError: (error: any) => {
      toast({
        title: "Sign In Failed",
        description: error.message || "Invalid email or password. Please try again.",
        variant: "destructive",
      });
    },
  });

  const magicLinkMutation = useMutation({
    mutationFn: async (emailData: { email: string }) => {
      return apiRequest("POST", "/api/auth/magic-link", emailData);
    },
    onSuccess: () => {
      setMagicLinkSent(true);
      toast({
        title: "Magic Link Sent!",
        description: "Check your email for a sign-in link.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send magic link. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleCredentialsSignIn = (e: React.FormEvent) => {
    e.preventDefault();
    if (!email || !password) return;
    credentialsSignInMutation.mutate({ email, password, rememberMe });
  };

  const handleMagicLinkRequest = (e: React.FormEvent) => {
    e.preventDefault();
    if (!email) return;
    magicLinkMutation.mutate({ email });
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center">
      <Card className="w-full max-w-md mx-4">
        <CardHeader className="text-center">
          <div className="mx-auto mb-4">
            <Link href="/">
              <a className="flex items-center justify-center space-x-3">
                <img 
                  src={diverWellLogo} 
                  alt="Professional Diver - Diver Well Training" 
                  className="w-12 h-12 rounded-lg"
                />
                <div>
                  <div className="text-lg font-bold text-slate-900">Professional Diver</div>
                  <div className="text-xs text-slate-500">Diver Well Training</div>
                </div>
              </a>
            </Link>
          </div>
          <CardTitle className="text-2xl font-bold text-gray-900" data-testid="text-signin-title">
            Sign In
          </CardTitle>
          <CardDescription className="text-slate-600">
            Access your diving education account
          </CardDescription>
        </CardHeader>
        <CardContent>
          {magicLinkSent && !isCredentials ? (
            <div className="text-center">
              <div className="mb-4">
                <Mail className="h-12 w-12 text-green-500 mx-auto mb-3" />
                <h3 className="text-lg font-semibold text-gray-900" data-testid="text-check-email">
                  Check Your Email
                </h3>
                <p className="text-slate-600 mt-2">
                  We sent a sign-in link to <strong>{email}</strong>. Click the link to access your account.
                </p>
              </div>
              <Button 
                variant="outline" 
                onClick={() => setMagicLinkSent(false)}
                className="w-full mb-2"
                data-testid="button-try-again"
              >
                Try Different Email
              </Button>
              <Button 
                variant="ghost" 
                onClick={() => setIsCredentials(true)}
                className="w-full text-sm"
                data-testid="button-use-password"
              >
                Use password instead
              </Button>
            </div>
          ) : (
            <>
              {/* Sign In Method Toggle */}
              <div className="flex border border-gray-200 rounded-lg mb-6" data-testid="signin-method-toggle">
                <button
                  onClick={() => setIsCredentials(true)}
                  className={`flex-1 py-2 px-4 text-sm font-medium rounded-l-lg transition-colors ${
                    isCredentials
                      ? "bg-primary-500 text-white"
                      : "text-slate-600 hover:text-slate-900"
                  }`}
                  data-testid="button-credentials"
                >
                  Password
                </button>
                <button
                  onClick={() => setIsCredentials(false)}
                  className={`flex-1 py-2 px-4 text-sm font-medium rounded-r-lg transition-colors ${
                    !isCredentials
                      ? "bg-primary-500 text-white"
                      : "text-slate-600 hover:text-slate-900"
                  }`}
                  data-testid="button-magic-link"
                >
                  Magic Link
                </button>
              </div>

              {/* Credentials Form */}
              {isCredentials ? (
                <form onSubmit={handleCredentialsSignIn} className="space-y-4">
                  <div>
                    <label htmlFor="email" className="block text-sm font-medium text-slate-700 mb-2">
                      Email Address
                    </label>
                    <Input
                      id="email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Enter your email"
                      required
                      className="w-full"
                      data-testid="input-email"
                    />
                  </div>

                  <div>
                    <label htmlFor="password" className="block text-sm font-medium text-slate-700 mb-2">
                      Password
                    </label>
                    <div className="relative">
                      <Input
                        id="password"
                        type={showPassword ? "text" : "password"}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                        required
                        className="w-full pr-10"
                        data-testid="input-password"
                      />
                      <button
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        className="absolute inset-y-0 right-0 pr-3 flex items-center"
                        data-testid="button-toggle-password"
                      >
                        {showPassword ? (
                          <EyeOff className="h-4 w-4 text-slate-400" />
                        ) : (
                          <Eye className="h-4 w-4 text-slate-400" />
                        )}
                      </button>
                    </div>
                  </div>

                  {/* Remember Me Checkbox */}
                  <div className="flex items-center space-x-2">
                    <Checkbox 
                      id="remember-me"
                      checked={rememberMe}
                      onCheckedChange={(checked) => setRememberMe(checked as boolean)}
                      data-testid="checkbox-remember-me"
                    />
                    <label 
                      htmlFor="remember-me" 
                      className="text-sm font-medium text-slate-700 cursor-pointer"
                    >
                      Remember me
                    </label>
                  </div>

                  <Button 
                    type="submit"
                    disabled={!email || !password || credentialsSignInMutation.isPending}
                    className="w-full bg-primary-500 hover:bg-primary-600 text-white"
                    data-testid="button-signin-credentials"
                  >
                    {credentialsSignInMutation.isPending ? "Signing In..." : "Sign In"}
                  </Button>
                </form>
              ) : (
                /* Magic Link Form */
                <form onSubmit={handleMagicLinkRequest} className="space-y-4">
                  <div>
                    <label htmlFor="magic-email" className="block text-sm font-medium text-slate-700 mb-2">
                      Email Address
                    </label>
                    <Input
                      id="magic-email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Enter your email"
                      required
                      className="w-full"
                      data-testid="input-magic-email"
                    />
                    <p className="text-xs text-slate-500 mt-1">
                      We'll send you a secure link to sign in without a password.
                    </p>
                  </div>

                  <Button 
                    type="submit"
                    disabled={!email || magicLinkMutation.isPending}
                    className="w-full bg-primary-500 hover:bg-primary-600 text-white"
                    data-testid="button-request-magic-link"
                  >
                    {magicLinkMutation.isPending ? (
                      <>
                        <Mail className="w-4 h-4 mr-2 animate-spin" />
                        Sending...
                      </>
                    ) : (
                      <>
                        <Mail className="w-4 h-4 mr-2" />
                        Send Magic Link
                      </>
                    )}
                  </Button>
                </form>
              )}

              {/* Demo Account Alert */}
              <Alert className="mt-6 border-primary-200 bg-primary-50">
                <AlertCircle className="h-4 w-4 text-primary-600" />
                <AlertDescription className="text-primary-700">
                  <strong>Demo Account:</strong> admin@diverwell.app / admin123
                </AlertDescription>
              </Alert>

              {/* Footer Links */}
              <div className="mt-6 text-center">
                <p className="text-sm text-slate-600">
                  Don't have an account?{" "}
                  <span className="text-slate-500">
                    Contact an administrator for an invitation.
                  </span>
                </p>
              </div>
            </>
          )}

          <div className="mt-6 pt-4 border-t border-gray-200 text-center">
            <Link href="/dashboard">
              <a className="text-sm text-primary-600 hover:text-primary-700" data-testid="link-back-dashboard">
                â† Back to Dashboard
              </a>
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

