// User management service for special accounts
export class UserManagementService {
  private specialUsers = new Map<string, any>();

  constructor() {
    this.initializeSpecialUsers();
  }

  // Initialize super admins and lifetime users
  private initializeSpecialUsers() {
    // Super Admin accounts
    const superAdmins = [
      {
        id: 'super-admin-1',
        email: 'lalabalavu.jon@gmail.com',
        name: 'Jon Lalabalavu',
        role: 'SUPER_ADMIN',
        subscriptionType: 'LIFETIME',
        subscriptionStatus: 'ACTIVE',
        specialAccess: true,
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      {
        id: 'super-admin-2', 
        email: 'sephdee@hotmail.com',
        name: 'Jon Lalabalavu',
        role: 'SUPER_ADMIN',
        subscriptionType: 'LIFETIME',
        subscriptionStatus: 'ACTIVE',
        specialAccess: true,
        createdAt: new Date(),
        updatedAt: new Date(),
      }
    ];

    // Lifetime access users for testing and marketing
    const lifetimeUsers = [
      {
        id: 'lifetime-1',
        email: 'freddierusseljoseph@yahoo.com',
        name: 'Freddie Russell Joseph',
        role: 'LIFETIME',
        subscriptionType: 'LIFETIME',
        subscriptionStatus: 'ACTIVE',
        specialAccess: true,
        purpose: 'Testing and Marketing',
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      {
        id: 'lifetime-2',
        email: 'deesuks@gmail.com', 
        name: 'Dilo Suka',
        role: 'LIFETIME',
        subscriptionType: 'LIFETIME',
        subscriptionStatus: 'ACTIVE',
        specialAccess: true,
        purpose: 'Testing and Marketing',
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      {
        id: 'lifetime-3',
        email: 'steve44hall@yahoo.co.uk',
        name: 'Steve Hall',
        role: 'LIFETIME',
        subscriptionType: 'LIFETIME',
        subscriptionStatus: 'ACTIVE',
        specialAccess: true,
        purpose: 'Testing and Marketing',
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      {
        id: 'lifetime-4',
        email: 'mike@ascotwood.com',
        name: 'Mike Scarpellini',
        role: 'LIFETIME',
        subscriptionType: 'LIFETIME',
        subscriptionStatus: 'ACTIVE',
        specialAccess: true,
        purpose: 'Testing and Marketing',
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      {
        id: 'lifetime-5',
        email: 'eroni2519@gmail.com', // Eroni Cirikidaveta
        name: 'Eroni Cirikidaveta',
        role: 'LIFETIME',
        subscriptionType: 'LIFETIME',
        subscriptionStatus: 'ACTIVE', // Email provided
        specialAccess: true,
        purpose: 'Testing and Marketing',
        note: 'Email address updated',
        createdAt: new Date(),
        updatedAt: new Date(),
      }
    ];

    // Store all special users
    [...superAdmins, ...lifetimeUsers].forEach(user => {
      this.specialUsers.set(user.email, user);
    });

    console.log('Initialized special users:', {
      superAdmins: superAdmins.length,
      lifetimeUsers: lifetimeUsers.length,
      total: this.specialUsers.size
    });
  }

  // Check if user has special access
  hasSpecialAccess(email: string): boolean {
    return this.specialUsers.has(email);
  }

  // Get user details by email
  getSpecialUser(email: string) {
    return this.specialUsers.get(email);
  }

  // Get all super admins
  getSuperAdmins() {
    return Array.from(this.specialUsers.values())
      .filter(user => user.role === 'SUPER_ADMIN');
  }

  // Get all lifetime users
  getLifetimeUsers() {
    return Array.from(this.specialUsers.values())
      .filter(user => user.role === 'LIFETIME');
  }

  // Update Eroni's email when provided
  updateEroniEmail(newEmail: string) {
    const eroniUser = Array.from(this.specialUsers.values())
      .find(user => user.name === 'Eroni Cirikidaveta');

    if (eroniUser) {
      // Remove old entry
      this.specialUsers.delete('eroni@pending.com');

      // Update email and status
      eroniUser.email = newEmail;
      eroniUser.subscriptionStatus = 'ACTIVE';
      eroniUser.note = 'Email address updated';
      eroniUser.updatedAt = new Date();

      // Store with new email
      this.specialUsers.set(newEmail, eroniUser);

      console.log(`Updated Eroni's email to: ${newEmail}`);
      return eroniUser;
    }

    return null;
  }

  // Get user role and access level
  getUserAccessLevel(email: string) {
    const user = this.getSpecialUser(email);

    if (!user) {
      return { role: 'USER', hasSpecialAccess: false };
    }

    return {
      role: user.role,
      hasSpecialAccess: user.specialAccess,
      subscriptionType: user.subscriptionType,
      subscriptionStatus: user.subscriptionStatus,
      purpose: user.purpose || null
    };
  }

  // List all special users for admin dashboard
  getAllSpecialUsers() {
    return Array.from(this.specialUsers.values())
      .sort((a, b) => {
        // Sort by role first (SUPER_ADMIN first), then by name
        if (a.role !== b.role) {
          return a.role === 'SUPER_ADMIN' ? -1 : 1;
        }
        return a.name.localeCompare(b.name);
      });
  }
}

export const userManagement = new UserManagementService();

shared/affiliate-schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, varchar, text, integer, timestamp, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { users } from "./schema";

// Affiliate program tables
export const affiliates = pgTable("affiliates", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id, { onDelete: "cascade" }),
  affiliateCode: varchar("affiliate_code").notNull().unique(),
  commissionRate: integer("commission_rate").default(50), // 50% commission
  totalReferrals: integer("total_referrals").default(0),
  totalEarnings: integer("total_earnings").default(0), // in cents
  monthlyEarnings: integer("monthly_earnings").default(0), // current month in cents
  isActive: boolean("is_active").default(true),
  referralLink: text("referral_link").notNull(),
  paypalEmail: text("paypal_email"),
  bankDetails: text("bank_details"), // encrypted bank details for payouts
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const referrals = pgTable("referrals", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  affiliateId: varchar("affiliate_id").notNull().references(() => affiliates.id, { onDelete: "cascade" }),
  referredUserId: varchar("referred_user_id").notNull().references(() => users.id, { onDelete: "cascade" }),
  affiliateCode: varchar("affiliate_code").notNull(),
  subscriptionType: text("subscription_type").notNull(), // MONTHLY, ANNUAL, LIFETIME
  monthlyValue: integer("monthly_value").notNull(), // subscription value in cents
  commissionEarned: integer("commission_earned").notNull(), // in cents
  status: text("status").default("ACTIVE").notNull(), // ACTIVE, CANCELLED, EXPIRED
  firstPaymentDate: timestamp("first_payment_date"),
  lastPaymentDate: timestamp("last_payment_date"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const commissionPayments = pgTable("commission_payments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  affiliateId: varchar("affiliate_id").notNull().references(() => affiliates.id, { onDelete: "cascade" }),
  amount: integer("amount").notNull(), // in cents
  paymentMethod: text("payment_method").notNull(), // PAYPAL, BANK_TRANSFER, STRIPE
  paymentReference: text("payment_reference"),
  paymentStatus: text("payment_status").default("PENDING").notNull(), // PENDING, COMPLETED, FAILED
  periodStart: timestamp("period_start").notNull(),
  periodEnd: timestamp("period_end").notNull(),
  paidAt: timestamp("paid_at"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const affiliateClicks = pgTable("affiliate_clicks", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  affiliateCode: varchar("affiliate_code").notNull(),
  ipAddress: text("ip_address"),
  userAgent: text("user_agent"),
  referrerUrl: text("referrer_url"),
  landingPage: text("landing_page"),
  converted: boolean("converted").default(false),
  convertedUserId: varchar("converted_user_id").references(() => users.id),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Insert schemas
export const insertAffiliateSchema = createInsertSchema(affiliates).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertReferralSchema = createInsertSchema(referrals).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertCommissionPaymentSchema = createInsertSchema(commissionPayments).omit({
  id: true,
  createdAt: true,
});

export const insertAffiliateClickSchema = createInsertSchema(affiliateClicks).omit({
  id: true,
  createdAt: true,
});

// Types
export type Affiliate = typeof affiliates.$inferSelect;
export type InsertAffiliate = z.infer<typeof insertAffiliateSchema>;
export type Referral = typeof referrals.$inferSelect;
export type InsertReferral = z.infer<typeof insertReferralSchema>;
export type CommissionPayment = typeof commissionPayments.$inferSelect;
export type InsertCommissionPayment = z.infer<typeof insertCommissionPaymentSchema>;
export type AffiliateClick = typeof affiliateClicks.$inferSelect;
export type InsertAffiliateClick = z.infer<typeof insertAffiliateClickSchema>;

